DROP TABLE IF EXISTS  `pre_common_member`;
CREATE TABLE `pre_common_member` (
	`uid` mediumint(8) UNSIGNED NOT NULL AUTO_INCREMENT,
	`email` char(40) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
	`username` char(15) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
	`password` char(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
	`status` tinyint(1) NOT NULL DEFAULT 0,
	`emailstatus` tinyint(1) NOT NULL DEFAULT 0,
	`avatarstatus` tinyint(1) NOT NULL DEFAULT 0,
	`videophotostatus` tinyint(1) NOT NULL DEFAULT 0,
	`adminid` tinyint(1) NOT NULL DEFAULT 0,
	`groupid` smallint(6) UNSIGNED NOT NULL DEFAULT 0,
	`groupexpiry` int(10) UNSIGNED NOT NULL DEFAULT 0,
	`extgroupids` char(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
	`regdate` int(10) UNSIGNED NOT NULL DEFAULT 0,
	`credits` int(10) NOT NULL DEFAULT 0,
	`notifysound` tinyint(1) NOT NULL DEFAULT 0,
	`timeoffset` char(4) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
	`newpm` smallint(6) UNSIGNED NOT NULL DEFAULT 0,
	`newprompt` smallint(6) UNSIGNED NOT NULL DEFAULT 0,
	`accessmasks` tinyint(1) NOT NULL DEFAULT 0,
	`allowadmincp` tinyint(1) NOT NULL DEFAULT 0,
	`onlyacceptfriendpm` tinyint(1) NOT NULL DEFAULT 0,
	`conisbind` tinyint(1) UNSIGNED NOT NULL DEFAULT 0,
	`freeze` tinyint(1) NOT NULL DEFAULT 0,
	PRIMARY KEY (`uid`),
	KEY `conisbind`(`conisbind`) USING BTREE,
	KEY `email`(`email`) USING BTREE,
	KEY `groupid`(`groupid`) USING BTREE,
	KEY `regdate`(`regdate`) USING BTREE,
	Unique KEY `username`(`username`) USING BTREE
) ENGINE=MyISAM
DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci
AUTO_INCREMENT=129
ROW_FORMAT=FIXED
CHECKSUM=0
DELAY_KEY_WRITE=0;
